// Obtener respuesta de Ollama
const ollamaResponse = $input.first().json;
const content = ollamaResponse.response;

// Parsear emoción
let emotion = 'NEUTRAL';
const emotionMatch = content.match(/EMOCIÓN:\s*(POSITIVO|NEUTRAL|NEGATIVO)/i);
if (emotionMatch) {
  emotion = emotionMatch[1].toUpperCase();
}

// Parsear engagement
let engagement = 0.5;
const engagementMatch = content.match(/ENGAGEMENT:\s*([\d.]+)/i);
if (engagementMatch) {
  engagement = Number.parseFloat(engagementMatch[1]);
  // Asegurar que está entre 0 y 1
  engagement = Math.max(0, Math.min(1, engagement));
}

// Extraer razón
let reason = '';
const reasonMatch = content.match(/RAZÓN:\s*(.+)/i);
if (reasonMatch) {
  reason = reasonMatch[1].trim();
}

// Retornar respuesta estructurada
return {
  json: {
    emotion: emotion,
    engagement: engagement,
    reason: reason,
    timestamp: new Date().toISOString()
  }
};
